import redMirageMap from './images/red_mirage_map.svg';

// Import PDFs
import resumePDF from './pdfs/joe_wlos_resume.pdf';

// FontAwesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
