import React, {
  useState,
  useEffect
} from 'react';
import aqua_headshot from './images/aqua_headshot.png';
import './css/App.css';

function App() {
  const [homeMessage, setHomeMessage] = useState(0);

  useEffect(() => {
    fetch('/api/v1/home_message').then(res => res.json()).then(data => {
      setHomeMessage(data.message);
    });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={ aqua_headshot } className="App-logo" alt="logo" />
        <p>{ homeMessage }</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
