// React
import React, {
    useState,
    useEffect
} from 'react';

// Bootstrap
import Nav from 'react-bootstrap/Nav';
import NavBar from 'react-bootstrap/NavBar';
import NavDropdown from 'react-bootstrap/NavDropdown';

// FontAwesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faFile,
  faBars
} from '@fortawesome/free-solid-svg-icons';
import { 
  faTwitterSquare,
  faLinkedin,
  faGithubSquare
} from '@fortawesome/free-brands-svg-icons';

// Import CSS for the navigation
import './css/Navigation.css';

// State for the homepage message and fill when we have scrolled
function Navigation() {
    const [scroll, setScroll] = useState(false)

    // Check if we have scrolled on the page
    useEffect(() => {
        document.addEventListener("scroll", () => {
            const scrollCheck = window.scrollY < 0;
            if ((scrollCheck !== scroll) || (scroll === false)) {
                setScroll(true);
                console.log('Triggered!');
            }
        })
    })

    // Return the JSX navigation
    return (
        <NavBar className="joe-navbar" expand="md" fixed="top">

            {/* Name */}
            <NavBar.Brand className="joe-brand" href="/">
                Joe Wlos
            </NavBar.Brand>

            {/* Toggle for mobile */}
            <NavBar.Toggle aria-controls="basic-navbar-nav" className="navbar-toggler-icon">
                <FontAwesomeIcon icon={ faBars } />
            </NavBar.Toggle>

            {/* Internal links */}
            <NavBar.Collapse id="basic-navbar-nav">
                <Nav>
                    <Nav.Link href="#action/2" className="joe-menu-item">About</Nav.Link>

                    {/* Dropdown Links */}
                    <NavDropdown title="Projects" id="basic-nav-dropdown" className="joe-menu-dropdown">
                        <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                        <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                        <NavDropdown.Divider />
                        <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                    </NavDropdown>

                {/* Close the internal links */}
                </Nav>
            </NavBar.Collapse>

            {/* External links */}
            <NavBar.Collapse id="basic-navbar-nav" className="justify-content-end joe-nav-pad">
                <Nav>

                    {/* FontAwesome */}
                    <Nav.Item className="joe-icon-row">
                        <a href="https://www.linkedin.com/in/joewlos/" className="joe-menu-icon"><FontAwesomeIcon icon={ faLinkedin } /></a>
                        <a href="https://twitter.com/joewlos?lang=en" className="joe-menu-icon"><FontAwesomeIcon icon={ faTwitterSquare } /></a>
                        <a href="https://github.com/joewlos" className="joe-menu-icon"><FontAwesomeIcon icon={ faGithubSquare } /></a>
                        <a href="#action/2" className="joe-menu-icon"><FontAwesomeIcon icon={ faFile } /></a>
                    </Nav.Item>

                {/* Close the navigation */}
                </Nav>
            </NavBar.Collapse>
        </NavBar>
    );}

export default Navigation;
