// React
import React, {
  useState,
  useEffect
} from 'react';

// Bootstrap
import Nav from 'react-bootstrap/Nav';
import NavBar from 'react-bootstrap/NavBar';
import NavDropdown from 'react-bootstrap/NavDropdown';

// FontAwesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faFile,
  faBars
} from '@fortawesome/free-solid-svg-icons';
import { 
  faTwitterSquare,
  faLinkedin,
  faGithubSquare
} from '@fortawesome/free-brands-svg-icons';

// Import CSS for the homepage
import './css/Home.css';

// State for the homepage message
function Home() {
  const [homeMessage, setHomeMessage] = useState(0);

  // Fetch the message from the API and update state
  useEffect(() => {
    fetch('/api/v1/home_message').then(res => res.json()).then(data => {
      setHomeMessage(data.message);
    });
  }, []);

  // Return the JSX navigation
  return (
    <NavBar className="joe-navbar" expand="lg" fixed="top">

      {/* Name */}
      <NavBar.Brand className="joe-brand" href="/">
        Joe Wlos
      </NavBar.Brand>

      {/* Toggle for mobile */}
      <NavBar.Toggle aria-controls="basic-navbar-nav" className="navbar-toggler-icon">
        <FontAwesomeIcon icon={ faBars } />
      </NavBar.Toggle>

      {/* Menu of links */}
      <NavBar.Collapse id="basic-navbar-nav" className="justify-content-end">
        <Nav>

          {/* Links */}
          <Nav.Link href="#action/2" className="joe-menu-item">About</Nav.Link>

          {/* Dropdown Links */}
          <NavDropdown title="Projects" id="basic-nav-dropdown" className="joe-menu-dropdown">
            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
          </NavDropdown>

          {/* FontAwesome */}
          <Nav.Link href="https://www.linkedin.com/in/joewlos/" className="joe-menu-item"><FontAwesomeIcon icon={ faLinkedin } /></Nav.Link>
          <Nav.Link href="https://twitter.com/joewlos?lang=en" className="joe-menu-item"><FontAwesomeIcon icon={ faTwitterSquare } /></Nav.Link>
          <Nav.Link href="https://github.com/joewlos" className="joe-menu-item"><FontAwesomeIcon icon={ faGithubSquare } /></Nav.Link>
          <Nav.Link href="#action/2" className="joe-menu-item navbar-new-right"><FontAwesomeIcon icon={ faFile } /></Nav.Link>

        {/* Close the navigation */}
        </Nav>
      </NavBar.Collapse>
    </NavBar>
  );
}

export default Home;
