// React
import React from 'react';

// Bootstrap
import { 
    Row, 
    Col
  } from 'react-bootstrap';

// Import components
import Navigation from './Navigation.js';
import Footer from './Footer.js';
import SunburstChart from './Sunburst.js'

// Import CSS for the page and content
import './css/Page.css';
import './css/RedMirage.css';

// Import images
import redMirageMap from './images/red_mirage_map.svg';

// Content for Red Mirage
function RedMirage() {

    // Sunburst data
    const vbmSunburst = {
        title: 'Voting Method',
        children: [
            {
                title: 'In Person',
                size: 0.49,
                color: '#e46f62',
                children: [
                    {
                        title: 'Biden',
                        size: 0.3,
                        color: 'blue'
                    },
                    {
                        title: 'Trump',
                        size: 0.62,
                        color: 'red'
                    },
                    {
                        title: 'Other',
                        size: 0.8,
                        color: 'grey'
                    }
                ]
            },
            {
                title: 'By Mail',
                size: 0.39,
                color: 'blue'
            },
            {
                title: 'Unsure',
                size: 0.12,
                color: 'green'
            }
        ]
    };

    // No transition for the navigation on pages
    return (
        <div>
            <Navigation transition={ false }/>

                {/* Content */}
                <div className='container-page'>
                    <div className='container-content'>

                        {/* Summary text */}
                        <Row className='align-items-center mb-5'>
                            <Col md={ 5 } className='text-left'>
                                <h1 className='page-title mb-5'>
                                    Anticipating the Red Mirage
                                </h1>
                                <p>
                                    As a data scientist at <a target="_blank" rel="noreferrer" href="https://www.linkedin.com/company/hawkfish/">Hawkfish</a> in 2020,
                                    my team polled Americans on their voting method preferences for the general election.
                                </p>
                                <p>
                                    A record number of voters intended to mail in or drop off their ballots to avoid the risk of coronavirus exposure at the polls. 
                                    However, many Trump supporters still planned to vote in person.
                                </p>
                                <p>
                                    Because in-person ballots were counted and reported first in most states, my team anticipated the <b><i>Red Mirage</i></b> on election night.
                                </p>
                                <p>
                                    We worked to innoculate the public against vote-by-mail disinformation
                                    and preempt Trump's false victory declaration.
                                </p>
                            </Col>
                            <Col md={ 7 } className='text-center'>
                                <img src={ redMirageMap } alt="Red Mirage" className='red-mirage-map' />
                                <p className='graph-subtitle mt-3'>
                                    On election night, Trump led in swing states where in-person ballots were counted and reported first.
                                    Four days later, after states had tallied mail-in ballots, Biden's victory was apparent.
                                </p>
                            </Col>
                        </Row>

                        {/* Analyzing our data */}
                        <Row className='align-items-center mb-3'>
                            <Col md={ 12 } className='text-left'>
                                <h1 className='page-subtitle'>
                                    Measuring the Partisan Gap in Voting Method Selection
                                </h1>
                            </Col>
                        </Row>

                        <Row className='align-items-center'>
                            <Col md={ 4 } className='text-center'>
                                <h1 className='graph-title mb-2'>
                                    Risk of voting in person during the pandemic?
                                </h1>
                                <div className='sunburst-fill'>
                                    <SunburstChart 
                                        data={ vbmSunburst }
                                    />
                                </div>
                            </Col>
                            <Col md={ 4 } className='text-center'>
                                <h1 className='graph-title mb-2'>
                                    Which method will you use to vote in the election?
                                </h1>
                                <div className='sunburst-fill'>
                                    <SunburstChart 
                                        data={ vbmSunburst }
                                    />
                                </div>
                            </Col>
                            <Col md={ 4 } className='text-center'>
                                <h1 className='graph-title mb-2'>
                                    Likelihood of substantial mail-in ballot fraud?
                                </h1>
                                <div className='sunburst-fill'>
                                    <SunburstChart 
                                        data={ vbmSunburst }
                                    />
                                </div>
                            </Col>
                        </Row>
                    </div>
                </div>

            {/* Close out the page with the footer */}
            <Footer />
        </div>
    );
}

export default RedMirage;
